import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const Login = () => {
  const history = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  //const [token, setToken] = useState("");
  let url = "https://rg-url-shortener-server.herokuapp.com";

  const handleClick = async () => {
    if (email !== "" && password !== "") {
      try {
        await axios
          .post(`${url}/user/login`, { email, password })
          .then((response) => {
            if (!response.data.token) {
              alert(response.data.message);
            } else {
              //setToken(response.data.token);
              history.push("/homepage");
            }
          });
        setEmail("");
        setPassword("");
      } catch (error) {
        console.log(error);
      }
    } else {
      alert("Please fill all the details");
    }
  };
  return (
    <div className="card card__">
      <p className="title">Sign-in</p>
      <div className="login-img">
        <img
          src="\imgs\hello.jpg"
          className="card-img-top real-img"
          alt="login-img"
        />
      </div>
      <div className="form-group">
        <label htmlFor="email">Email address</label>
        <input
          type="email"
          className="form-control"
          id="email"
          placeholder="Enter email"
          value={email}
          onChange={(e) => {
            setEmail(e.target.value);
          }}
        />
      </div>
      <div className="form-group">
        <label htmlFor="pass">Password</label>
        <input
          type="password"
          className="form-control"
          id="pass"
          placeholder="Enter password"
          value={password}
          onChange={(e) => {
            setPassword(e.target.value);
          }}
        />
      </div>

      <button
        type="submit"
        className="btn btn-success my-2"
        onClick={handleClick}
      >
        Login
      </button>

      <div className="additional-options">
        <Link to="/create-account" className="option-list">
          Create new account
        </Link>

        <Link to="/forgot-password" className="option-list">
          Forgot Password?
        </Link>
      </div>
    </div>
  );
};

export default Login;
